import React from "react";
import "./TodoListItem.scss";

const TodoListItem = ({ label, important = false }) => {
  const style = {
    color: important ? "tomato" : "black",
  };
  return (
    <span>
      <span style={style}>{label}</span>
      <span className="itemBtns">
        <p>add</p>
        <p>del</p>
      </span>
    </span>
  );
};

export default TodoListItem;
