import React, { Component } from "react";
import "./App.scss";

// Компоненты
import TodoHeader from "./components/TodoHeader/TodoHeader";
import SearchPanel from "./components/SearchPanel/SearchPanel";
import TodoList from "./components/TodoList/TodoList";

class App extends Component {
  maxId = 100;
  state = {
    logoName: ["logo", "Changed"],
    i: 0,
    todoData: [
      {
        label: "Написать Туду Приложение",
        important: true,
        id: 1,
        done: false,
      },
      { label: "Выпить кофе", important: false, id: 2, done: false },
      { label: "Убраться в квартире", important: false, id: 3, done: false },
    ],
  };
  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const newArr = [...this.state.todoData];
      const idx = todoData.findIndex((el) => el.id === id);
      newArr.splice(idx, 1);
      // alert("delited id:" + id + "test" + idx);
      return {
        todoData: newArr,
      };
    });
  };
  addItem = (text) => {
    this.setState(({ todoData }) => {
      let newItem = {
        label: text,
        important: false,
        id: this.maxId++,
        done: false,
      };
      console.log(text);
      const newArr = [newItem, ...this.state.todoData];
      return {
        todoData: newArr,
      };
    });
  };
  render() {
    return (
      <div className="App">
        <TodoHeader title={"Todo"} />
        <SearchPanel addTodos={this.addItem} />
        <TodoList
          todos={this.state.todoData}
          leftCase={1}
          succedCase={3}
          onDeleted={this.deleteItem}
        />
      </div>
    );
  }
}

export default App;
