import React, { Component } from "react";
import "./TodoListItem.scss";

export default class TodoListItem extends Component {
  state = {
    done: false,
    important: false,
  };
  onLabelClick = () => {
    // alert(`Done: ${this.props.label} `);
    this.setState((state) => {
      return {
        done: !state.done,
      };
    });
  };
  onMarkImportant = () => {
    this.setState(({ important }) => {
      return { important: !important };
    });
  };

  render() {
    const { label, onDeleted } = this.props;
    const { done, important = false } = this.state;
    const style = {
      color: important ? "#0065eb" : "black",
      fontWeight: important ? 600 : 300,
    };
    let classNames = "";
    if (done) {
      classNames += " done";
    }
    if (important) {
      classNames += " important";
    }

    return (
      <span className={"itemContainer "}>
        <span style={style} onClick={this.onLabelClick} className={classNames}>
          {label}
        </span>
        <span className="itemBtns">
          <h1 onClick={onDeleted}>-</h1>
          <h1 onClick={this.onMarkImportant}>!</h1>
        </span>
      </span>
    );
  }
}
